/**
 * Re-export Film icon from Lucide for movies.
 * Use this for consistency with the TV icon.
 */
export { Film as MovieIcon };

/**
 * Media type badge component with precise centering using CSS grid.
 * Use this wrapper for consistent icon positioning in circular badges.
 */
interface MediaTypeBadgeProps {
  mediaType: 'movie' | 'tv';
  className?: string;
  size?: 'sm' | 'md';
}

export function MediaTypeBadge({ mediaType, className = '', size = 'md' }: MediaTypeBadgeProps) {
  const sizeClasses = size === 'sm'
    ? 'w-5 h-5'
    : 'w-6 h-6';
  const iconClasses = size === 'sm'
    ? 'w-3 h-3 text-white'
    : 'w-3.5 h-3.5 text-white';

  return (
    <div className={`${sizeClasses} bg-black/75 backdrop-blur-sm rounded-full grid place-items-center ${className}`}>
      {mediaType === 'movie' ? (
        <Film className={`${iconClasses} block`} />
      ) : (
        <TvIcon className={iconClasses} />
      )}
    </div>
  );
}
