// Brand colors for each provider (official brand colors)
export const STREAMING_COLORS: Record<number, string> = {
  8: '#E50914', // Netflix red
  15: '#1CE783', // Hulu green
  1899: '#002BE7', // Max blue
  337: '#113CCF', // Disney+ blue
  386: '#181818', // Peacock (multicolor, use dark bg)
  9: '#00A8E1', // Prime Video blue
  2: '#181818', // Apple TV+ dark
  531: '#0064FF', // Paramount+ blue
  73: '#FA382F', // Tubi orange-red
  300: '#181818', // Pluto TV dark
  283: '#F47521', // Crunchyroll orange
  350: '#181818', // Apple TV dark
};

// Provider ID to Icon component mapping
type IconComponent = React.FC<IconProps>;

const STREAMING_ICONS: Record<number, IconComponent> = {
  8: ({ size, color, className }) => (
    <SiNetflix size={size} color={color} className={className} />
  ),
  15: HuluIcon,
  1899: MaxIcon,
  337: DisneyPlusIcon,
  386: PeacockIcon,
  9: PrimeVideoIcon,
  2: ({ size, color, className }) => (
    <SiAppletv size={size} color={color} className={className} />
  ),
  531: ({ size, color, className }) => (
    <SiParamountplus size={size} color={color} className={className} />
  ),
  73: ({ size, color, className }) => (
    <SiTubi size={size} color={color} className={className} />
  ),
  300: PlutoTVIcon,
  283: ({ size, color, className }) => (
    <SiCrunchyroll size={size} color={color} className={className} />
  ),
  350: ({ size, color, className }) => (
    <SiAppletv size={size} color={color} className={className} />
  ),
};

// Scale factors to visually balance icon sizes
const WIDE_LOGO_SCALE: Record<number, number> = {
  8: 1.1, // Netflix - slightly bigger
  15: 1.9, // Hulu - wide wordmark
  337: 2.4, // Disney+ - detailed wide logo
  386: 1.6, // Peacock - detailed feathers
  9: 1.6, // Prime Video - wide wordmark
  2: 1.6, // Apple TV+ - thin wordmark
  1899: 1.6, // Max - stacked text logo
  531: 1.3, // Paramount+ - mountain with text
  350: 1.6, // Apple TV - thin wordmark
};

interface StreamingServiceIconProps {
  providerId: number;
  className?: string;
  size?: number;
  showBackground?: boolean;
  colored?: boolean;
}

export default function StreamingServiceIcon({
  providerId,
  className = '',
  size = 24,
  showBackground = false,
  colored = false,
}: StreamingServiceIconProps) {
  const IconComponent = STREAMING_ICONS[providerId];
  const brandColor = STREAMING_COLORS[providerId] || '#374151';
  const scale = WIDE_LOGO_SCALE[providerId] || 1;

  if (!IconComponent) {
    // Fallback: colored circle with question mark
    return (
      <div
        className={`flex items-center justify-center rounded ${className}`}
        style={{
          width: size,
          height: size,
          backgroundColor: brandColor,
        }}
      >
        <span className="text-white text-xs font-bold">?</span>
      </div>
    );
  }

  if (showBackground) {
    const iconSize = size * 0.7 * scale;
    return (
      <div
        className={`flex items-center justify-center rounded overflow-hidden ${className}`}
        style={{
          width: size,
          height: size,
          backgroundColor: brandColor,
          padding: size * 0.15,
        }}
      >
        <IconComponent size={iconSize} color="#ffffff" />
      </div>
    );
  }

  return (
    <IconComponent
      size={size * scale}
      color={colored ? brandColor : undefined}
      className={className}
    />
  );
}

export { STREAMING_ICONS };
